```css
/* Global styles */

body {
  margin: 0;
  font-family: sans-serif;
}

/* Header styles */

header {
  background-color: #0077be;
  color: #ffffff;
  padding: 10px;
}

header h1 {
  margin: 0;
  font-size: 36px;
}

/* Main styles */

.main-container {
  display: grid;
  grid-template-columns: repeat(2, 1fr);
  grid-column-gap: 30px;
  padding: 30px;
}

.section {
